github_storage = "https://raw.githubusercontent.com/FacultadInformatica-LinkedData/Curso2021-2022/master/Assignment4/course_materials"

#"""Leemos el fichero RDF de la forma que lo hemos venido haciendo"""

from rdflib import Graph, Namespace, Literal
from rdflib.namespace import RDF, RDFS
g = Graph()
g.namespace_manager.bind('ns', Namespace("http://somewhere#"), override=False)
g.namespace_manager.bind('vcard', Namespace("http://www.w3.org/2001/vcard-rdf/3.0#"), override=False)
g.parse(github_storage+"/rdf/example6.rdf", format="xml")

#"""**TASK 7.1: List all subclasses of "Person" with RDFLib and SPARQL**"""

# TO DO
# Visualize the results
from rdflib.plugins.sparql import prepareQuery
ns=Namespace("http://somewhere#")

for r in g.triples((None,RDFS.subClassOf,ns.Person)):
  print(r)
print("################")

q1 = prepareQuery('''
  SELECT ?Subject WHERE { 
    ?Subject rdfs:subClassOf ns:Person . 
  }
  ''',
  initNs = { "rdfs": RDFS,"ns":ns}
)

for r in g.query(q1):
  print(r)

#"""**TASK 7.2: List all individuals of "Person" with RDFLib and SPARQL (remember the subClasses)**

#"""

# TO DO
# Visualize the results
from rdflib.plugins.sparql import prepareQuery
ns=Namespace("http://somewhere#")

for r in g.triples((None,RDF.type,ns.Person)):
  print(r)
for s,p,o in g.triples((None,RDFS.subClassOf,ns.Person)):
    for r1 in g.triples((None,RDF.type,s)):
      print(r1)
  
print("################")

q1 = prepareQuery('''
  SELECT ?Subject ?p WHERE { 
    {
      ?Subject rdf:type ns:Person .
    }
    UNION
    {
      ?S rdfs:subClassOf ns:Person.
      ?p rdf:type ?S
    }
  }
  ''',
  initNs = { "rdfs": RDFS,"ns":ns}
)

for r in g.query(q1):
  print(r)

#"""**TASK 7.3: List all individuals of "Person" and all their properties including their class with RDFLib and SPARQL**

#"""

# TO DO
# Visualize the results
from rdflib.plugins.sparql import prepareQuery
ns=Namespace("http://somewhere#")

for s,p,o in g.triples((None,RDF.type,ns.Person)):
  for r in g.triples((s,None,None)):
    print(r)
for s,p,o in g.triples((None,RDFS.subClassOf,ns.Person)):
    for s1,p1,o1 in g.triples((None,RDF.type,s)):
      for r1 in g.triples((s1,None,None)):
       print(r1)
print("#######################################")

q1 = prepareQuery('''
  SELECT ?Subject ?p ?z  ?class WHERE { 
    {?Subject rdf:type ns:Person .
    ?Subject ?p ?z.}
    UNION
    {
      ?class rdfs:subClassOf ns:Person.
      ?Subject rdf:type ?class.
      ?Subject ?p ?z.
    }
  }
  ''',
  initNs = { "rdf":RDF,"rdfs": RDFS,"ns":ns}
)

for r in g.query(q1):
  print(r)
